    input clk,
    input [7:0] duty_cycle,
    output pwm_out
);
    reg [7:0] counter = 8'b0;
    reg out = 1'b0;
    always @(posedge clk) begin
        counter <= counter + 1;
        if (counter == duty_cycle)
