module div_32bit(
    input wire [31:0] A,   // 32-bit dividend
    input wire [15:0] B,   // 16-bit divisor
    output wire [31:0] result, // 32-bit quotient
    output wire [31:0] odd      // 32-bit remainder
);
    wire [31:0] A_prep;
    wire [15:0] B_prep;
    wire [31:0] result_prep;
    wire [31:0] odd_prep;
    // Instantiate the submodules
    prep_dividend #(.WIDTH_A(32), .WIDTH_B(16))
                  prep_dividend_inst(
                      .A(A),
                      .B(B),
           	      .A_prep(A_prep),
                      .B_prep(B_prep)
                  );
    div_operation #(.WIDTH_A(32), .WIDTH_B(16))
                  div_operation_inst(
                      .A(A_prep),
                      .B(B_prep),
                      .result(result_prep),
                      .odd(odd_prep)
                  );
    // Assign the outputs
    assign result = result_prep;
    assign odd = odd_prep;
endmodule
module prep_dividend #(parameter WIDTH_A = 32, WIDTH_B = 16)
                     (
    input wire [WIDTH_A-1:0] A,
    input wire [WIDTH_B-1:0] B,
    output wire [WIDTH_A-1:0] A_prep,
    output wire [WIDTH_B-1:0] B_prep
);
    // Adjust the bit widths of A and B
    assign A_prep = {A[WIDTH_A-1], A[WIDTH_A-1:1]};
    assign B_prep = B;
endmodule
module div_operation #(parameter WIDTH_A = 32, WIDTH_B = 16)
                     (
    input wire [WIDTH_A-1:0] A,
    input wire [WIDTH_B-1:0] B,
    output wire [WIDTH_A-1:0] result,
    output wire [WIDTH_A-1:0] odd
);
    reg [WIDTH_A-1:0] A_reg;
    reg [WIDTH_B-1:0] B_reg;
    reg [WIDTH_A-1:0] result_reg;
    reg [WIDTH_A-1:0] odd_reg;
    integer i;
    always @(A or B) begin
        A_reg = A;
        B_reg = B;
        result_reg = 0;
        odd_reg = 0;
        for (i = 0; i < WIDTH_A; i = i + 1) begin
            // Shift the dividend
            A_reg = A_reg << 1;
            // Compare the upper bits of the dividend with the divisor
            if (A_reg[WIDTH_A-1:WIDTH_A-WIDTH_B] >= B_reg) begin
                // Subtract the divisor from the upper bits of the dividend
                A_reg[WIDTH_A-1:WIDTH_A-WIDTH_B] = A_reg[WIDTH_A-1:WIDTH_A-WIDTH_B] - B_reg;
                // Set the current bit in the quotient
                result_reg[WIDTH_A-1-i] = 1'b1;
            end
        end
        odd_reg = A_reg;
    end
    assign result = result_reg;
    assign odd = odd_reg;
endmodule

