module div_operation #(parameter WIDTH_A = 32, parameter WIDTH_B = 16)
                     (
    input wire [WIDTH_A-1:0] A,
    input wire [WIDTH_B-1:0] B,
    output reg [WIDTH_A-1:0] result,
    output reg [WIDTH_A-1:0] odd
);
    reg [WIDTH_A-1:0] A_temp;
    reg [WIDTH_B-1:0] B_temp;
    reg [5:0] shift_count; // 6-bit shift register for counting up to 32 shifts
    always @(A, B) begin
        A_temp = A;
        B_temp = B;
        shift_count = 0;
        result = 0;
        odd = 0;
        while (shift_count < WIDTH_A) begin
            A_temp = A_temp << 1;
            if (A_temp >= {16'b0, B_temp}) begin
                A_temp = A_temp - {16'b0, B_temp};
                result = (result << 1) | 1'b1;
            end else begin
                result = result << 1;
            end
            shift_count = shift_count + 1;
        end
        odd = A_temp;
    end
endmodule

