module arithmetic_logic(
    input clk,
    input rst,
    input [3:0] op_code,
    input [3:0] data_a,
    input [3:0] data_b,
    output reg [3:0] result
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            result <= 4'b0000;
        end else begin
            case (op_code)
                4'b0001: result <= data_a + data_b;
                4'b0010: result <= data_a - data_b;
                4'b0100: result <= data_a & data_b;
                4'b0101: result <= data_a | data_b;
                default: result <= 4'b0000;
            endcase
        end
    end
endmodule
module op_decoder(
    input [1:0] op_code,
    output reg [3:0] decoded_op
);
    always @(*) begin
        case (op_code)
            2'b00: decoded_op = 4'b0001;
            2'b01: decoded_op = 4'b0010;
            2'b10: decoded_op = 4'b0100;
            2'b11: decoded_op = 4'b0101;
            default: decoded_op = 4'b0000;
        endcase
    end
endmodule
module result_manager(
    input [3:0] result,
    output reg zero
);
    always @(*) begin
        zero = (result == 4'b0000);
    end
endmodule

