    wire [15:0] sub_result;
    wire [15:0] mul_result;
    wire [15:0] div_result;
    wire add_valid;
    wire sub_valid;
    wire mul_valid;
    wire div_valid;
    add_module adder(
        .clk(clk),
        .rst(rst),
        .data_a(data_a),
        .data_b(data_b),
        .result(add_result),
        .valid(add_valid)
    );
    sub_module subtractor(
        .clk(clk),
        .rst(rst),
        .data_a(data_a),
        .data_b(data_b),
        .result(sub_result),
        .valid(sub_valid)
    );
    mul_module multiplier(
        .clk(clk),
        .rst(rst),
        .data_a(data_a),
        .data_b(data_b),
        .result(mul_result),
        .valid(mul_valid)
    );
    div_module divider(
        .clk(clk),
        .rst(rst),
        .data_a(data_a),
        .data_b(data_b),
        .result(div_result),
        .valid(div_valid)
    );
    always @(*) begin
        case (opcode)
            2'b00: begin
                result <= add_result;
                valid <= add_valid;
            end
            2'b01: begin
                result <= sub_result;
                valid <= sub_valid;
            end
            2'b10: begin
                result <= mul_result;
                valid <= mul_valid;
            end
            2'b11: begin
                result <= div_result;
                valid <= div_valid;
            end
        endcase
    end
endmodule
