    wire [4:1] P, G, B;
    assign P = A ^ B;
    assign G = A & B;
    assign B[1] = G[1] | (P[1] & B_in);
    assign B[2] = G[2] | (P[2] & B[1]);
    assign B[3] = G[3] | (P[3] & B[2]);
    assign B[4] = G[4] | (P[4] & B[3]);
    assign D = P ^ {B_in, B};
    assign B_out = B[4:1];
endmodule

